function base64ToPage(base64, str, filePath) {
  var file = _fs["default"].readFile(filePath, function (err, data) {
    if (err) {
      console.error(err);
    } else {
      var html = data.toString('utf8');
      if (str === 'foto') {
        html = html.replace("{{foto}}", base64);
      }
      if (str === 'certidao') {
        html = html.replace("{{certidao}}", base64);
      }
      _fs["default"].writeFile(filePath, html, function (err) {
        if (err) {
          console.error(err);
        } else {
          console.log('exibe ', str);
        }
      });
    }
  });
}
