function converte(arquivo){
    if(arquivo!==null){
        try{
            let filepath = `/politec/sicp/${arquivo}`; 
            let buff = fileSystem.readFileSync(filepath);
            let base64data = buff.toString('base64');
            return base64data;
        }
        catch(err){
            console.log();
            return null;
        }
    }    
}

